import React from 'react';
import { connect } from 'react-redux';
import { get4Options, increaseQuizScore } from '../actions';

class DisplayOptions extends React.Component {

    componentDidMount() {
        this.props.get4Options(this.props.currentStep);
    }

    componentDidUpdate(prevProps) {
        if(prevProps.currentStep !== this.props.currentStep) {
            this.props.get4Options(this.props.currentStep);
        }
    }

    onSelection(userSelection) {
        if(userSelection === this.props.correctBreed) {

        }
    }

    renderOptions() {
        if(this.props.fourOptions) {
            return this.props.fourOptions.map(option => {
                return (
                    <div className="field" key={option}>
                        <div className="ui radio checkbox">
                            <input type="radio" value={option} name="example2" onChange={e=>this.onSelection(e.target.value)}/>
                            <label>{option}</label>
                        </div>
                    </div>
                );
            })
        }
    }

    render() {
        return (
        <div className="ui form">
            <div className="grouped fields">
                {this.renderOptions()}
            </div>
        </div>   
        );
    }
}

const mapStateToProps = (state, ownProps) => {
    return {
        fourOptions: state.dogState.fourOptions,
        currentStep: state.quiz.quizStep,
        correctBreed: state.dogState.breed
    }
}

export default connect(mapStateToProps, { get4Options, increaseQuizScore })(DisplayOptions);
